using System;
using System.Collections.Generic;
using System.Drawing.Drawing2D;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Archivos
{
    internal class Alumnos
    {// defino como va a ser el registro vana ser las propiedades
        public long DNI { get; private set; }//long es un entero de 64 bits (DNI 9-10 digitos)
        public string Nombre { get; set; }
        public string Apellido { get; set; }

        //constructor es un metodo especial en una clase, se ejecuta autoaticamente cuando creas una instancia
        //de esta clase en este caso alumnos, su proposito esta en inicializar el objeto, es decir asigna valores iniciales
        //a las propiedades del objeto
        //al usar new el constructor se ejecuta sin que lo llames explicitamente
        //Sin constructor, el objeto se crea con valores por defecto (ej. null para strings, 0 para números),
        //y podrías tener datos inconsistentes. El constructor asegura que el objeto empiece en un estado válido.
        //constructor creo el objeto alumno solo sabiendo el dni, recibe el parametro dni
        public Alumnos(long dni) { 
            // para asociar al alumno con su dni
            this.DNI = dni;
        }

        //constructor creo el objeto alumno, paso por parametro una linea de string(un registro)
        public Alumnos(string linea) {
            //
            string[] datos = linea.Split(',');//va a dividir la linea por comas DNi,Apellido,Nombre
            this.DNI = long.Parse(datos[0]);//asigna dni a la posicion 0 y lo convierte en long
            this.Apellido = datos[1];//asigna apellido a la posicion 1
            this.Nombre = datos[2];//asigna nombre a la posicion 2                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    

        }
        //establezco como quiero que se vea los datos en la grilla
        public String GenerarRegistro() {
            return $"{DNI}, {Apellido}, {Nombre}";
        }
    }
}
